configuration.js:

				$(document).ready(function() {
			document.getElementsByClassName('logo')[0].style.display='none'
			Array.from(document.querySelectorAll("div"))
			.filter(el => el.textContent.startsWith('Request Details'))
			.forEach(myFancyResult => {myFancyResult.style.textDecoration = "underline"
                myFancyResult.style.color = "#000000" });
			Array.from(document.querySelectorAll("div")).filter(el => el.textContent.startsWith('Response Details'))
			.forEach(myFancyResult => {myFancyResult.style.textDecoration = "underline"
                myFancyResult.style.color = "#000000" });
            Array.from(document.querySelectorAll("div")).filter(el => el.textContent.startsWith('!!'))
			.forEach(myFancyResult => {myFancyResult.style.textDecoration = "underline"
                myFancyResult.style.color = "#000000" });
            Array.from(document.querySelectorAll("div")).filter(el => el.textContent.startsWith('Info'))
			.forEach(myFancyResult => {myFancyResult.style.color="#ff9500"});
			Array.from(document.querySelectorAll("div")).filter(el => el.textContent.startsWith('AssertFail'))
			.forEach(myFancyResult => {myFancyResult.style.color="red"});
			Array.from(document.querySelectorAll("div")).filter(el => el.textContent.startsWith('AssertPass'))
			.forEach(myFancyResult => {myFancyResult.style.fontSize = "medium"
				myFancyResult.style.color="#24d83a"});
				});
				let table=document.getElementsByClassName("card-body pb-0 pt-0")[0];
			let tb=table.getElementsByTagName("tbody")[0];
			let tr=tb.querySelectorAll("tr");
			for (let t of tr){
				if (!t.firstElementChild.textContent.includes('api')){
					t.style.display = "none"
			}}

